create table T_SYS_ORG
(
    ORG_CODE VARCHAR(60) not null primary key,
    FATHER_ORG_CODE VARCHAR(60),
    ORG_NAME        VARCHAR(200),
    CONTACT_NAME    VARCHAR(60),
    CONTACT_PHONE   VARCHAR(60),
    `email`           VARCHAR(200),
    ORG_ADDR        VARCHAR(900),
    PROVINCE        VARCHAR(10),
    STATUS          VARCHAR(2)  not null,
    NUM             VARCHAR(60),
    UNION_ORG_CODE  VARCHAR(60),
    SYNC_FLAG       VARCHAR(1),
    CREATED_BY      VARCHAR(32),
    CREATED_TIME    TIMESTAMP(6),
    UPDATED_BY      VARCHAR(32),
    UPDATED_TIME    TIMESTAMP(6),
    ORG_LEVEL       numeric(2)
) comment  '机构信息表';

